task main()
{
	int power = 10;
   //a for loop is declared that: intializes i equal to zero
   //continues to run under the condition i is less than 20
   //and increments i by one after each iteration
			motor[motorBL] = power;    // Motor D is run at a	75 power level.
      motor[motorFL] = power;
      motor[motorBR] = -power;    // Motor D is run at a	75 power level.
      motor[motorFR] = -power;
      wait1Msec(1500);

      motor[motorBL] = power;    // Motor D is run at a	75 power level.
      motor[motorFL] = 0;
      motor[motorBR] = -power;    // Motor D is run at a	75 power level.
      motor[motorFR] = 0;
      wait1Msec(1500);

      motor[motorBL] = -power;    // Motor D is run at a	75 power level.
      motor[motorFL] = -power;
      motor[motorBR] = power;    // Motor D is run at a	75 power level.
      motor[motorFR] = power;
      wait1Msec(1500);

motor[motorBL] = power;    // Motor D is run at a	75 power level.
      motor[motorFL] = power;
      motor[motorBR] = power;    // Motor D is run at a	75 power level.
      motor[motorFR] = power;
      wait1Msec(1500);

      motor[motorBL] = -power;    // Motor D is run at a	75 power level.
      motor[motorFL] = -power;
      motor[motorBR] = -power;    // Motor D is run at a	75 power level.
      motor[motorFR] = -power;
      wait1Msec(1500);
      }
